import { useEffect, useState } from 'react';
import axios from '../api/axiosInstance';
import { Link, useNavigate } from 'react-router-dom'; // ✅ Add useNavigate
import './Dashboard.css';

export default function Dashboard() {
  const [files, setFiles] = useState([]);
  const [userId, setUserId] = useState('');
  const navigate = useNavigate(); // ✅ Initialize navigate

  useEffect(() => {
    const storedUserId = localStorage.getItem('user_id');
    if (storedUserId) {
      setUserId(storedUserId);
    }
  }, []);

  useEffect(() => {
    if (!userId) return;

    axios.get(`/get-files/?user_id=${userId}`)
      .then(res => setFiles(res.data))
      .catch(() => alert('Error fetching files'));
  }, [userId]);

  // ✅ Logout handler
  const handleLogout = () => {
    localStorage.removeItem('user_id');
    navigate('/'); // ✅ Go to Welcome page
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1 className="dashboard-title">Your Documents</h1>
        <button onClick={handleLogout} className="logout-button">Logout</button>
      </div>

      <input
        type="text"
        placeholder="Enter your user ID"
        className="input-userid"
        value={userId}
        onChange={(e) => setUserId(e.target.value)}
      />

      <Link to="/upload">
        <button className="upload-button">Upload New Document</button>
      </Link>

      {files.length === 0 ? (
        <p className="no-files">No files uploaded yet.</p>
      ) : (
        <ul className="file-list">
          {files.map(file => (
            <li key={file.id} className="file-item">
              <span className="file-name">{file.filename}</span>
              <a
                href={`http://localhost:8000/download/${file.id}`}
                className="file-download-link"
                target="_blank"
                rel="noreferrer"
              >
                Download
              </a>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
