import { useNavigate } from 'react-router-dom';
import { useRef, useState } from 'react';
import './Welcome.css';

// Font Awesome imports
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faInstagram, faLinkedin, faTwitter } from '@fortawesome/free-brands-svg-icons';

export default function Welcome() {
  const navigate = useNavigate();
  const contactRef = useRef(null);
  const socialsRef = useRef(null);

  const scrollToContact = () => {
    contactRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const scrollToSocials = () => {
    socialsRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  // Formspree logic
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });
  const [submitted, setSubmitted] = useState(false);

  const FORM_ENDPOINT = 'https://formspree.io/f/your-form-id'; // Replace with your actual Formspree endpoint

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch(FORM_ENDPOINT, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    });

    if (response.ok) {
      setSubmitted(true);
      setFormData({ name: '', email: '', message: '' });
    } else {
      alert('Error submitting the form. Please try again.');
    }
  };

  return (
    <div className="welcome-layout">
      {/* Sidebar (Fixed) */}
      <div className="sidebar">
        <h1>DocuPort</h1><br />
        <p>Portfolio Storage Made Simple !</p>
        <p>No login hassle. Just upload, store, and access.</p>
        <button onClick={() => navigate('/login')}>Go to Login</button>

        <ul className="nav-list">
          <li>üìÇ Instant Access</li>
          <li>üìä Analytics</li>
          <li>üîí Secure Storage</li>
        </ul>

        <ul className="nav-list2">
          <li className="link" onClick={scrollToContact}>üìû Contact</li>
          <li className="link" onClick={scrollToSocials}>üì± Socials</li>
        </ul>
      </div>

      {/* Right Scrollable Content */}
      <div className="scrollable-content">
        {/* Hero Section */}
        <div className="hero-section">
          <img src="https://i.ytimg.com/vi/TwYKwaEjJd4/maxresdefault.jpg" alt="Intro visual" />
        </div>

        {/* Spacer Section */}
        <div className="spacer-section">
          <h2>Features</h2>
          <p>üìÅ Upload files instantly</p>
          <p>üìä View real-time analytics</p>
          <p>üîê Securely store your portfolio documents</p>
          <p>üåê Access anywhere, anytime</p>
          <p>üìÑ Supports all file types</p>
          <p>üß† AI-powered search (coming soon)</p>
          <p style={{ marginBottom: '500px' }}>üìà Organize projects by tags and categories</p>
        </div>

        {/* Contact Section */}
        <div ref={contactRef} className="contact-section">
          <h2>Contact Us</h2>
          <form className="contact-form" onSubmit={handleSubmit}>
            <input
              type="text"
              name="name"
              placeholder="Your Name"
              value={formData.name}
              onChange={handleChange}
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Your Email"
              value={formData.email}
              onChange={handleChange}
              required
            />
            <textarea
              name="message"
              placeholder="Your Message"
              value={formData.message}
              onChange={handleChange}
              required
            ></textarea>
            <button type="submit">Send</button>
            {submitted && <p className="success-msg">Thanks! We'll be in touch soon.</p>}
          </form>
        </div>

        {/* Socials Section */}
        <div ref={socialsRef} className="socials-section">
          <h2>Connect with us</h2><br />
          <div className="social-icons">
            <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">
              <FontAwesomeIcon icon={faInstagram} size="2x" />
            </a>
            <a href="https://linkedin.com" target="_blank" rel="noopener noreferrer">
              <FontAwesomeIcon icon={faLinkedin} size="2x" />
            </a>
            <a href="https://twitter.com" target="_blank" rel="noopener noreferrer">
              <FontAwesomeIcon icon={faTwitter} size="2x" />
            </a>
          </div>
        </div>
      </div>
    </div>
  );
}
