import { useNavigate } from 'react-router-dom';
import { useRef, useEffect, useState } from 'react';
import { useAuth } from '../context/AuthContext';


import './Welcome.css';


import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faInstagram, faLinkedin, faTwitter } from '@fortawesome/free-brands-svg-icons';


export default function Welcome() {
const navigate = useNavigate();
const { user } = useAuth();


const contactRef = useRef(null);
const socialsRef = useRef(null);
const aboutRef = useRef(null);


const scrollToContact = () => contactRef.current?.scrollIntoView({ behavior: 'smooth' });
const scrollToSocials = () => socialsRef.current?.scrollIntoView({ behavior: 'smooth' });
const scrollToAbout = () => aboutRef.current?.scrollIntoView({ behavior: 'smooth' });


const [formData, setFormData] = useState({ name: '', email: '', message: '' });
const [submitted, setSubmitted] = useState(false);


const FORM_ENDPOINT = 'https://formspree.io/f/xovdyabo';


const handleChange = (e) => {
const { name, value } = e.target;
setFormData(prev => ({ ...prev, [name]: value }));
};


const handleSubmit = async (e) => {
e.preventDefault();


const response = await fetch(FORM_ENDPOINT, {
method: 'POST',
headers: { 'Content-Type': 'application/json' },
body: JSON.stringify(formData),
});


if (response.ok) {
setSubmitted(true);
setFormData({ name: '', email: '', message: '' });
} else {
alert('Error submitting the form. Please try again.');
}
};


const handleLoginClick = () => {
if (user) {
navigate('/dashboard');
} else {
navigate('/login');
}
};


const [inView, setInView] = useState(false);
const [inViewFeatures, setInViewFeatures] = useState(false);
const [inViewInfoImages, setInViewInfoImages] = useState(false);


useEffect(() => {
const observer = new IntersectionObserver(([entry]) => setInView(entry.isIntersecting), { threshold: 0.3 });
const section = document.getElementById('aboutSection');
if (section) observer.observe(section);
return () => section && observer.unobserve(section);
}, []);


useEffect(() => {
const observer = new IntersectionObserver(([entry]) => setInViewFeatures(entry.isIntersecting), { threshold: 0.3 });
const section = document.getElementById('featureSection');
if (section) observer.observe(section);
return () => section && observer.unobserve(section);
}, []);


useEffect(() => {
const observer = new IntersectionObserver(([entry]) => setInViewInfoImages(entry.isIntersecting), { threshold: 0.3 });
}, []);

  return (
    <div className="welcome-layout">
      {/* Sidebar (Fixed) */}
      <div className="sidebar">
        <h1>DocuPort</h1><br />
        <p>Document storage made <br /><span style={{ color: '#118ab2' }}>Simple !</span></p><br />
        <p>Login with otp. Just upload, store and access, anytime & anywhere.</p>
        <br />
        <div class="nav-list-bar">
            <div class="nav-list">
              <ul className="nav-list">
                <li>üìÇ<br /> Instant <br />Access</li>
              </ul>
            </div>

            <div class="nav-list">
              <ul className="nav-list">
                <li>üìä<br /> Ease of <br /> Usage</li>
              </ul>
            </div>

            <div class="nav-list">
              <ul className="nav-list">
                <li>üîí<br /> Secure <br />Storage</li>
              </ul>
            </div>
        </div>

        <button onClick={() => navigate('/login')}>Login</button>
        <ul className="nav-list2">
          <li className="link" onClick={scrollToAbout}>üë§ About</li>
          <li className="link" onClick={scrollToContact}>üìû Contact</li>
          <li className="link" onClick={scrollToSocials}>üì± Socials</li>
        </ul>

      </div>

      {/* Right Scrollable Content */}
      <div className="scrollable-content">
        {/* Hero Section */}
        <div className="hero-section">
          <img src="Docuport.png" alt="Intro visual" />
        </div><br />
        <p class="description">We help you store personal documents<br /> with us securely</p>
      
        
      {/* Small Icons Section */}
      <div class="small-icons">
        <div class="small-icon">
          <img src="upload.png" alt="Upload" />
          <p>Upload</p>
        </div>
        <div class="small-icon">
          <img src="access.png" alt="Access" />
          <p>Access</p>
        </div>
        <div class="small-icon">
          <img src="store.png" alt="Store" />
          <p>Store</p>
        </div>
      </div>



      {/* About Section */}
      <div ref={aboutRef} id="aboutSection" className={`about ${inView ? 'animate-about' : ''}`}>
        <div className="about-text">
          <h1>About</h1>
          <p>
            DocuPort is a digital document management solution that prioritizes confidentiality and integrity of sensitive information. It offers advanced encryption and authentication protocols, preventing risks like limited accessibility, inconvenience, and environmental impact. Users can create documents for easy retrieval and collaborate in real-time, providing efficient document handling. DocuPort offers cloud storage, a user-friendly interface, and security measures. This project enhanced critical thinking, problem-solving, teamwork, and time management abilities.
          </p>
        </div>
        <img src="information.jpg" alt="secondary_visuals" />
      </div>
      
      <div id="infoimageSection" className={`info-images ${inViewInfoImages ? 'animate-infoimages' : ''}`}>
        <img src="section1.jpg" alt="section1" />
        <img src="section2.jpg" alt="section2" />
      </div>


        {/* Features Section */}
       <div id="featureSection" className={`features ${inViewFeatures ? 'animate-features' : ''}`}>
        <img src="features.jpg" alt="secondary_visuals" />
        <div className="features-text">
          <h1>Features</h1>
          <p>üìÅ Upload files instantly</p>
          <p>üîê Securely store your portfolio documents</p>
          <p>üåê Access anywhere, anytime</p>
          <p>üìÑ Supports all file types</p>
          <p>üß† AI-powered search (coming soon)</p>
        </div>
      </div>

      <h1 class="heading-info">First Class Website</h1>
      <p class="description-2">Experience seamless access and robust security <br />for all your important documents organized, protected, <br />and available whenever you need them,<br /> with us securely.</p>
      {/* Info Icons Section */}
      <div class="info-icons">
        <div class="info-icon">
          <img src="storage.png" alt="self-storage" />
          <p>Self Storage</p>
        </div>
        <div class="info-icon">
          <img src="secure.png" alt="secure" />
          <p>Secure</p>
        </div>
        <div class="info-icon">
          <img src="anywhere.png" alt="anywhere-access" />
          <p>Access Anywhere</p>
        </div>
        <div class="info-icon">
          <img src="login.png" alt="login" />
          <p>OTP Login</p>
        </div>
      </div>

      <div className="image-container">
        <img src="footer.jpg" alt="image" className="image" />
        <div className="image-text">Join with us now & enjoy <br />the services.</div>
      </div><br />


      <div class="footer-section">
                {/* Contact Section */}
        <div ref={contactRef} className="contact-section">
          <h2>Contact Us</h2><br />
          <form className="contact-form" onSubmit={handleSubmit}>
            <input
              type="text"
              name="name"
              placeholder="Your Name"
              value={formData.name}
              onChange={handleChange}
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Your Email"
              value={formData.email}
              onChange={handleChange}
              required
            />
            <textarea
              name="message"
              placeholder="Your Message"
              value={formData.message}
              onChange={handleChange}
              required
            ></textarea>
            <button type="submit">Send</button>
            {submitted && <p className="success-msg">Thanks! We'll be in touch soon.</p>}
          </form>
        </div>

        {/* Socials Section */}
        <div ref={socialsRef} className="socials-section">
          <h2>Connect with us</h2>
          <div className="social-icons">
            <a href="https://www.instagram.com/rutvviik?igsh=YzRibWN6dGMzbXl1" target="_blank" rel="noopener noreferrer">
              <FontAwesomeIcon icon={faInstagram} size="2x" />
            </a>
            <a href="https://www.linkedin.com/in/rutvik-pradhan-59040724b/" target="_blank" rel="noopener noreferrer">
              <FontAwesomeIcon icon={faLinkedin} size="2x" />
            </a>
            <a href="https://x.com/Rutvikkkkkkkkk?t=AraxdfgJo4Y-veaehvPo7Q&s=09" target="_blank" rel="noopener noreferrer">
              <FontAwesomeIcon icon={faTwitter} size="2x" />
            </a>
          </div>
      </div>
        </div>
      </div>
    </div>
  );
}
