import { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import Upload from './pages/Upload';
import Welcome from './pages/Welcome';

function AppWrapper() {
  const [userId, setUserId] = useState(localStorage.getItem('user_id'));

  return <App userId={userId} setUserId={setUserId} />;
}

function App({ userId, setUserId }) {
  const navigate = useNavigate();

  const handleLogin = (id) => {
    localStorage.setItem('user_id', id);
    setUserId(id);
  };

  const handleLogout = () => {
    localStorage.removeItem('user_id');
    setUserId(null);
    navigate('/'); // ✅ Redirect to Welcome page
  };

  return (
    <div>
      <nav className="p-4 bg-blue-600 text-white flex justify-between">
        <span>{userId ? `Welcome, User ${userId}` : 'DocuPort'}</span>
        {userId && (
          <button onClick={handleLogout} className="bg-red-500 px-3 py-1 rounded">
            Logout
          </button>
        )}
      </nav>

      <Routes>
        <Route path="/" element={<Welcome />} />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/dashboard" element={userId ? <Dashboard /> : <Navigate to="/login" />} />
        <Route path="/upload" element={userId ? <Upload /> : <Navigate to="/login" />} />
      </Routes>
    </div>
  );
}

export default function RootApp() {
  return (
    <Router>
      <AppWrapper />
    </Router>
  );
}
